mapper.select:

select no, email, name
from users
where email=#{email}


select no, name, type
from users
where email=#{email} and password=#{password}


select no, email, name, gender
from users
where no=#{no}

mapper.update:

update users
set



	password=#{password},



name=#{name},
gender=#{gender}
where no=#{no}
